mapper.insert:
INSERT INTO PROJECT (
       PROPOSER_ID
     , NAME
     , AMOUNT
     , PERIOD
     , DEADLINE
     , START_DATE
     , CONTENT
     , STATUS
     , CREATED_DATE
     , MODIFIED_DATE
) VALUES (
       #{proposerId}
     , #{name}
     , #{amount}
     , #{period}
     , #{deadline}
     , #{startDate}
     , #{content}
     , #{status}
     , now()
     , now()
)

mapper.update:
UPDATE PROJECT
   SET NAME = #{name}
     , AMOUNT = #{amount}
     , PERIOD = #{period}
     , DEADLINE = #{deadline}
     , START_DATE = #{startDate}
     , CONTENT = #{content}
     , MODIFIED_DATE = now()
 WHERE ID = #{id}
